<?php class Database { private $host = 'localhost'; private $db_name = 'modrive'; private $user = 'root'; private $password = ''; private $conn; public function connect() { $this->conn = null; $dsn = 'mysql:host=' . $this->host . ';dbname=' . $this->db_name; $options = array(PDO::ATTR_PERSISTENT => true, PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION); try { $this->conn = new PDO($dsn, $this->user, $this->password, $options); } catch(PDOException $e) { echo 'Connection Error: ' . $e->getMessage(); } return $this->conn; } } ?>